mapper.select:
select ID id, FROM_ACCOUNT fromAccount, FROM_CLIENT_TYPE fromClientType, MSGID_CLIENT msgidClient, FROM_EXT fromExt, FROM_AVATOR fromAvator, FROM_NICK fromNick,MSG_TIMESTAMP msgTimestamp, RESEND_FLAG resendFlag, MSG_TYPE msgType, CHAT_ROOM_ID chatRoomId, QUIZ_OR_REPLY quizOrReply, REPLY_ID replyId, REPLY_NAME replyName,REPLY_AVATOR replyAvator, ATTACH attach,CUSTOM_MSG_TYPE customMsgType,IS_GARBAGE isGarbage,COURSE_ID courseId,LIVE_TOPIC liveTopic from chat_room_msg
IS_GARBAGE = '0' AND COURSE_ID= #{courseId}
AND MSG_TIMESTAMP < #{chatRoomMsg.msgTimestamp}

select ID id, FROM_ACCOUNT fromAccount, FROM_CLIENT_TYPE fromClientType, MSGID_CLIENT msgidClient, FROM_EXT fromExt, FROM_AVATOR fromAvator, FROM_NICK fromNick,MSG_TIMESTAMP msgTimestamp, RESEND_FLAG resendFlag, MSG_TYPE msgType, CHAT_ROOM_ID chatRoomId, QUIZ_OR_REPLY quizOrReply, REPLY_ID replyId, REPLY_NAME replyName,REPLY_AVATOR replyAvator, ATTACH attach,CUSTOM_MSG_TYPE customMsgType,IS_GARBAGE isGarbage,COURSE_ID courseId,LIVE_TOPIC liveTopic from chat_room_msg
IS_GARBAGE = '0' AND COURSE_ID= #{courseId}
AND MSG_TIMESTAMP < #{.msgTimestchatRoomMsgamp}
AND FROM_ACCOUNT=#{chatRoomMsg.fromAccount} and CUSTOM_MSG_TYPE='0'

SELECT ID , FROM_AVATOR , FROM_NICK , ATTACH , MSG_TIMESTAMP , MSGID_CLIENT , FROM_ACCOUNT FROM chat_room_msg
IS_GARBAGE = '0' AND CUSTOM_MSG_TYPE = '5' AND QUIZ_OR_REPLY = '1' AND COURSE_ID = #{courseId} and ATTACH != '' AND ID < #{msgId}
ORDER BY MSG_TIMESTAMP DESC LIMIT #{limit}

SELECT ID , FROM_AVATOR , FROM_NICK , ATTACH , MSG_TIMESTAMP ,FROM_ACCOUNT , MSGID_CLIENT FROM chat_room_msg

    IS_GARBAGE = '0' AND CUSTOM_MSG_TYPE = '0' AND COURSE_ID = #{courseId}
    AND FROM_ACCOUNT != #{teacherId} and ATTACH != ''
    AND ID < #{msgId}

ORDER BY MSG_TIMESTAMP DESC
LIMIT #{limit}

SELECT ID ,  FROM_AVATOR , FROM_NICK ,  ATTACH , MSG_TIMESTAMP ,
FROM_ACCOUNT , CUSTOM_MSG_TYPE , MSGID_CLIENT FROM chat_room_msg
where CUSTOM_MSG_TYPE in ('0','5') and ATTACH != ''
    and   IS_GARBAGE = '0'
    AND FROM_ACCOUNT != #{teacherId}  AND  COURSE_ID = #{courseId}
    ORDER BY MSG_TIMESTAMP  DESC LIMIT #{limit}

SELECT ID ,  FROM_AVATOR , FROM_NICK ,  ATTACH , MSG_TIMESTAMP ,
       FROM_ACCOUNT , CUSTOM_MSG_TYPE , MSGID_CLIENT FROM chat_room_msg
where CUSTOM_MSG_TYPE in ('0','5') and ATTACH != ''
and   IS_GARBAGE = '0'
AND FROM_ACCOUNT = #{teacherId}  AND  COURSE_ID = #{courseId}
ORDER BY MSG_TIMESTAMP  DESC LIMIT #{limit}

