FROM chat_room_msg

    IS_GARBAGE = '0' AND
    ((FROM_ACCOUNT = #{teacherId} AND CUSTOM_MSG_TYPE IN ('13','14' , '0'))
    OR CUSTOM_MSG_TYPE = '11'
    ) AND COURSE_ID = #{courseId} and ATTACH != ''
    AND ID < #{msgId}

ORDER BY MSG_TIMESTAMP DESC
LIMIT #{pageSize}
